preferences.postscript:
if (Translator.BetterBibLaTeX) {
  if (tex.has.title)
    tex.add({ name: 'title', value: zotero.title.replace(/(\$.*?\$)/g, '<script>{$1}</script>') })

  if (tex.has.shorttitle)
    tex.add({ name: 'shorttitle', value: zotero.shorttitle.replace(/(\$.*?\$)/g, '<script>{$1}</script>') })

  if (tex.has.issn || tex.has.doi || zotero.arXiv || tex.has.isbn)
    delete tex.has.url

  if (tex.has.doi) {
    delete tex.has.issn
    delete tex.has.issn
  }

  if (tex.has.doi && zotero.arxiv) {
    delete tex.has.eprint
    delete tex.has.eprinttype
    delete zotero.arxiv
  }

  if (tex.has.doi && tex.has.isbn)
    delete tex.has.doi
}

if (Translator.BetterBibTeX) {
  if (tex.has.issn || tex.has.doi || zotero.arXiv || tex.has.isbn)
    delete tex.has.url

  if (tex.has.doi) {
    delete tex.has.issn
    delete tex.has.issn
  }

  if (tex.has.doi && zotero.arxiv) {
    delete tex.has.eprint
    delete zotero.arxiv
  }

  if (tex.has.doi && zotero.arxiv) {
    delete tex.has.eprinttype
    delete zotero.arxiv
  }

  if (tex.has.isbn)
    delete tex.has.doi
}
