preferences.postscript:
if (Translator.BetterTeX) {
  function marklatex(note) {
    return note
      .replace(/<pre class="math">.*?<\/pre>/ig, pre => pre.replace(/\\/g, '\x1a')) // save display math or it would get marked twice
      .replace(/(\\(?:[a-z]+|[^a-z])(?:\[[^\]]*\])*(?:\{[^\}]*\})*)/ig, '<script>$1</script>') // find latex commands and mark them as literal output
      .replace(/\x1a/g, '\\') // restore display math
  }
  tex.add({ name: 'note', value: zotero.notes?.map(note => marklatex(note.note)).join('</p><p>'), html: true })
}
