preferences.postscript:
if (Translator.BetterBibTeX && item.itemType === 'conferencePaper' && item.extra) {
  for (const line of item.extra.split('\n')) {
    try {
      const [ , keyword, value ] = line.match(/([^:]+):s*(.*)/)
      switch (keyword) {
        case 'Sponsor':
          reference.add({name: 'organization', value })
          break
      }
    } catch (err) {
      if (!(err instanceof TypeError)) throw err
    }
  }
}
